cotisation.map:
function(doc) {
if(doc.cotisations.length > 0) {
    for(var idx in doc.cotisations) {
      emit(doc._id, {_id:doc._id, dateh:doc.cotisations[idx].dateh, datef:doc.cotisations[idx].datef, amount:doc.cotisations[idx].amount});
    }
  }
}

cotisationAmount.map:
function(doc) {
if(doc.cotisations.length > 0) {
    for(var idx in doc.cotisations) {
      emit(doc.cotisations[idx].year, doc.cotisations[idx].amount);
    }
  }
}

cotisationCount.map:
function(doc) {
if(doc.cotisations.length > 0) {
    for(var idx in doc.cotisations) {
      emit(doc.cotisations[idx].year, 1);
    }
  }
}

cotisationYear.map:
function(doc) {
if(doc.cotisations.length > 0) {
    for(var idx in doc.cotisations) {
      emit(doc.cotisations[idx].year, {_id:doc._id, login:doc.login, Firstname:doc.Firstname, Lastname:doc.Lastname, dateh:doc.cotisations[idx].dateh, datef:doc.cotisations[idx].datef, note:doc.cotisations[idx].note, amount:doc.cotisations[idx].amount});
    }
  }
}

tag.map:
function(doc) {
if(doc.Tag.length > 0) {
    for(var idx in doc.Tag) {
      emit(doc.Tag[idx], 1);
    }
  }
}

updates.in-place:
function (doc, req) {
 var field = req.query.field;
 var value = req.query.value;
 doc[field] = value;
  return [doc, value];
 }
