list.map:
function(doc) {
if (doc.class && doc.class == "Commande" && doc.trash != true)
  emit(doc._id, doc);
}

count.map:
function(doc) {
if (doc.class && doc.class == "Commande" && doc.trash != true)
  emit(doc.ref, 1);
}

count_status.map:
function(doc) {
if (doc.class && doc.class == "Commande" && doc.trash != true)
  emit(doc.Status, 1);
}

listLinkedObjects.map:
function(doc) {
if (doc.class && (doc.class == "Propal" || doc.class == "Commande" || doc.class == "Facture") && doc.trash != true) {
for (i in doc.linked_objects)
emit(doc.linked_objects[i].id, doc);
}
}

countByDate.map:
function(doc) {
if (doc.class && doc.class == "Commande" && doc.trash != true){
emit(doc.date, 1);
}
}

listByDate.map:
function(doc) {
if (doc.class && doc.class == "Commande" && doc.trash != true){
emit(doc.date, doc);
}
}

amountByDate.map:
function(doc) {
if (doc.class && doc.class == "Commande" && doc.trash != true){
emit(doc.date, doc.total_ttc);
}
}

averageByDate.map:
function(doc) {
if (doc.class && doc.class == "Commande" && doc.trash != true){
emit(doc.date, doc.total_ttc);
}
}

listBySociete.map:
function(doc) {
if (doc.class && doc.class == 'Commande' && doc.trash != true)
  emit(doc.client.id, doc);
}

lines.map:
function(doc) {
if (doc.class && doc.class == "Commande" && doc.trash != true ){
for (i in doc.lines) {
	var id;
	id=doc._id +"#"+ i;
  emit(doc._id, {_id:id,ref:doc.ref,description:doc.lines[i].description,pu_ht:doc.lines[i].pu_ht,qty:doc.lines[i].qty,tva_tx:doc.lines[i].tva_tx,total_ht:doc.lines[i].total_ht,total_tva:doc.lines[i].total_tva,total_ttc:doc.lines[i].total_ttc,product_type:doc.lines[i].product_type,remise:doc.lines[i].remise,product:doc.lines[i].product,group:doc.lines[i].group});
}
 }
}

history.map:
function(doc) {
if(doc.class && doc.class == "Commande" && doc.trash != true && doc.history.length > 0) {
    for(var idx in doc.history) {
      emit(doc._id, {date:doc.history[idx].date, author:doc.history[idx].author, Status:doc.history[idx].Status});
    }
  }
}

updates.in-place:
function (doc, req) {
 var field = req.query.field;
 var value = req.query.value;
 doc[field] = value;
  return [doc, value];
 }
