default_right.map:
function(doc) {
  if(doc.class=="DolibarrModules" && doc.rights && doc.rights_class && doc.enabled) {
     var i=0;
     doc.rights.forEach(function(tag) {
         emit([doc.rights_class,tag.id],{_id:doc._id,numero:doc.numero,name:doc.name,desc:tag.desc,rights_class:doc.rights_class,Status:tag.default,id:tag.id, perm:tag.perm, picto:doc.picto, idx:i});
         i++;
    });
}
}

list.map:
function(doc) {
  if(doc.class && doc.class=="DolibarrModules")
    emit(doc.name, {numero:doc.numero, enabled:doc.enabled, always_enabled:doc.always_enabled, family: doc.family, version: doc.version, picto: doc.picto, moddir:doc.moddir, dirs:doc.dirs, depends:doc.depends, requiredby:doc.requireby,phpmin:doc.phpmin, need_dolibarr_version:doc.need_dolibarr_version, const:doc.const, langfiles:doc.langfiles,boxes:doc.boxes})
}

list_export.map:
function(doc) {
  if(doc.class=="DolibarrModules" && doc.export && doc.enabled) {
     doc.export.forEach(function(tag) {
         emit([doc.name,tag.code],{_id:doc._id,numero:doc.numero,name:doc.name,langfiles:doc.langfiles,label:tag.label,permission:tag.permission,icon:tag.icon, class:tag.class});
    });
}
}
