submenu.map:
function(doc) {
  if(doc.class=="DolibarrModules" && doc.menus && doc.enabled) {
     doc.menus.forEach(function(tag) {
	if(!tag.type) {
      emit([tag.fk_menu,tag.position],tag);
	}
    });
}
}

list.map:
function(doc) {
  if(doc.class=="DolibarrModules" && doc.menus && doc.enabled) {
     doc.menus.forEach(function(tag) {
	if(tag.type) {
      emit([tag.position,tag._id],tag);
	}
    });
}
}
