list.map:
function(doc) {
  if(doc.class && doc.class=="Product"){
     var price = {};
     if(doc.price["BASE"])
       price = doc.price["BASE"];

     var docu = {};
     for(x in doc)
        if(x!="price")
	  docu[x] = doc[x];
        else
          docu[x] = price;
     delete docu.history;

     emit(doc.ref, docu);
   }
}

tag.map:
function(doc) {
if(doc.class=="Product" && doc.Tag.length > 0) {
    for(var idx in doc.Tag) {
      emit(doc.Tag[idx], 1);
    }
  }
}

listPriceLevel.map:
function(doc) {
if(doc.class=="Product" && doc.price) {
    for(idx in doc.price) {
      	emit(idx,1);
    }
  }
}

listHistoryPrices.map:
function(doc) {
if(doc.class=="Product" && doc.history.length > 0) { 
    for(var idx in doc.history) {
        var docu = {};
        for(var x in doc.history[idx])
    	  docu[x] = doc.history[idx][x];
	var id;
	docu._id=doc._id +"#"+ idx;
      emit(doc._id,docu);
    }
  }
}

listPrices.map:
function(doc) {
if(doc.class=="Product" && doc.price) { 
    for(var idx in doc.price) {
        var docu = {};
        for(var x in doc.price[idx])
    	  docu[x] = doc.price[idx][x];
	var id;
	docu._id=doc._id +"#"+ idx;
        docu.price_level = idx;
      emit(doc._id,docu);
    }
  }
}

updates.in-place:
function (doc, req) {
 var field = req.query.field;
 var value = req.query.value;
 doc[field] = value;
  return [doc, value];
 }
