tag.map:
function(doc) {
if(doc.class=="Product" && doc.Tag.length > 0) {
    for(var idx in doc.Tag) {
      emit(doc.Tag[idx], 1);
    }
  }
}

listPrice.map:
function(doc) {
if(doc.class=="Product" && doc.price.length > 0) {
    for(var idx in doc.price) {
      emit([doc.price[idx].PriceLevel,doc.price[idx].tms],doc.price[idx]);
    }
  }
}

listPriceLevel.map:
function(doc) {
if(doc.class=="Product" && doc.price.length > 0) {
    for(var idx in doc.price) {
      if(doc.price[idx].PriceLevel)
      	emit(doc.price[idx].PriceLevel,1);
    }
  }
}

updates.in-place:
function (doc, req) {
 var field = req.query.field;
 var value = req.query.value;
 doc[field] = value;
  return [doc, value];
 }
