lines.map:
function(doc) {
if (doc.class && doc.class == "Propal"){
for (i in doc.lines) {
	var id;
	id=doc._id +"#"+ i;
  emit(doc._id, {_id:id,description:doc.lines[i].description,pu_ht:doc.lines[i].pu_ht,qty:doc.lines[i].qty,tva_tx:doc.lines[i].tva_tx,total_ht:doc.lines[i].total_ht,total_tva:doc.lines[i].total_tva,total_ttc:doc.lines[i].total_ttc,product_type:doc.lines[i].product_type,remise:doc.lines[i].remise,product:doc.lines[i].product});
}
 }
}

listLinkedObjects.map:
function(doc) {
if (doc.class && (doc.class == "Propal" || doc.class == "Commande" || doc.class == "Facture")) {
for (i in doc.linked_objects)
emit(doc.linked_objects[i].id, doc);
}
}

updates.in-place:
function (doc, req) {
 var field = req.query.field;
 var value = req.query.value;
 doc[field] = value;
  return [doc, value];
 }
