listSociete.map:
function(doc) {
  if(doc.class && doc.class=="Contact" && doc.societe.id)
  emit(doc.societe.id, doc);
}

tag.map:
function(doc) {
if(doc.class=="Contact" && doc.Tag.length > 0) {
    for(var idx in doc.Tag) {
      emit(doc.Tag[idx], 1);
    }
  }
}

updates.in-place:
function (doc, req) {
 var field = req.query.field;
 var value = req.query.value;
 doc[field] = value;
  return [doc, value];
 }
