list.map:
function(doc) {
  if(doc.class && doc.class=="Societe" && doc._id != "societe:mysoc")
  emit(doc.name, doc);
}

list_customer_id.map:
function(doc) {
  if(doc.class && doc.code_client && doc.class=="Societe")
  emit(doc.code_client, doc._id);
}

tag.map:
function(doc) {
if(doc.class=="Societe" && doc.Tag.length > 0) {
    for(var idx in doc.Tag) {
      emit(doc.Tag[idx], 1);
    }
  }
}

updates.in-place:
function (doc, req) {
 var field = req.query.field;
 var value = req.query.value;
 doc[field] = value;
  return [doc, value];
 }
