group.map:
function(doc) {
  if(doc.class && doc.class=="User" && doc.group.length > 0) {
    for(var idx in doc.group) {
      emit(doc.group[idx], doc);
    }
  }
}

updates.in-place:
function (doc, req) {
 var field = req.query.field;
 var value = req.query.value;
 doc[field] = value;
  return [doc, value];
 }
