list.map:
function(doc) {
  if(doc.class && doc.class == "User" && doc.Status=="ENABLE")
     emit(doc.name, doc);
}

group.map:
function(doc) {
  if(doc.class && doc.class=="User" && doc.roles.length > 0) {
    for(var idx in doc.roles) {
      emit(doc.roles[idx], doc);
    }
  }
}

listAll.map:
function(doc) {
  if(doc.class && doc.class == "User" && doc._id != "user:searchengine")
     emit(doc.name, doc);
}

updates.in-place:
function (doc, req) {
 var field = req.query.field;
 var value = req.query.value;
 doc[field] = value;
  return [doc, value];
 }
